/** *  */package com.mytest.utils.servlet.request;/** * Api接口调用验证所需参数实体 *  * @author da.zhang * @date 2013-1-9 */public class CheckNeedParameter {	/**	 * Hmac签名	 */	private String sign;		/**	 * 请求时间戳	 */	private String time;		/**	 * 参数串	 */	private String parameters;	public String getSign() {		return sign;	}	public void setSign(String sign) {		this.sign = sign;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}	public String getParameters() {		return parameters;	}	public void setParameters(String parameters) {		this.parameters = parameters;	}}